import React from "react";
import { Text, TouchableOpacity, View } from 'react-native';
 
const logUserIn = async () => {
    try {
      await AsyncStorage.setItem("isLoggedIn", "true");
      setIsLoggedIn(true);
    } catch (e) {
      console.log(e);
    }
    }
  
const logUserOut = async () => {
    try {
      await AsyncStorage.setItem("isLoggedIn", "false");
      setIsLoggedIn(false);
    } catch (e) {
      console.log(e);
    }
}
  
export default () => {
    return <Text> <View style={{ flex: 1, justifyContent: "center", alignItems: "center" }}>
      {isLoggedI === true ?
        //로그인됐을때
        <TouchableOpacity onPress={logUserOut}>
          <Text>Log Out ? (☞ﾟヮﾟ)☞ <AntDesign name="staro" size={24} color="black" /> 
          </Text>
        </TouchableOpacity>
        :
        //로그아웃됐을때
        <TouchableOpacity onPress={logUserIn}>
          <Text>Log In, plz ಥ_ಥ <AntDesign name="star" size={24} color="black" /></Text>
        </TouchableOpacity>
        
      }  
        </View></Text>
}
